class Solution {
public:
    long long func(vector<int>& nums,int k){
        int n=nums.size();
        int ad=INT_MAX;
        long long c=0;
        int sum[31]={0};
    int i=0; int j=0;
    while(j<n){
        ad=ad&nums[j];

        for(int k=0;k<31;k++){
            if(nums[j]>>k&1) sum[k]++;
        }

            while(i<=j&&ad<k){
                int ch=0;
            //    if(k==2) cout<<i<<' ';
                for(int k=0;k<31;k++){
                    if(nums[i]>>k&1) sum[k]--;
                    if(sum[k]==j-i) ch=ch+(1<<k);
                }
                ad=ch;
                i++;
              }
            //  cout<<i;
        c+=j-i+1;
        j++;
    }

    return c;
    }


    long long countSubarrays(vector<int>& nums, int k) {
        // cout<<func(nums,k);
        return func(nums,k)-func(nums,k+1);

    }
};
