class Solution {
public:

    void add(int num,vector<int>&bits)
    {
        int j=0;
        for(int i=0;i<=30;i++)
        {
            if(num&(1<<i))
            {
               bits[i]++;
            }
        }
    }
    void del(int num,vector<int>&bits)
    {
        int j=0;
        for(int i=0;i<=30;i++)
        {
            if(num&(1<<i))
            {
               bits[i]--;
            }
        }
    }
    long long value(int len,vector<int>&bits)
    {
        int j=0;
        long long ans=0;
        for(int i=0;i<=30;i++)
        {
            if(bits[i]==len)ans=ans+1ll*(1<<i);
        }
        return ans;
    }


    long long atleastk(vector<int>v,int k)
    {
        int n=v.size();
        vector<int>bits(32,0);

        int i=0;
        int j=0;

        long long int ans=0;
        long long int temp=0;
        while(j<n)
        {
            add(v[j],bits);
            temp=value(j-i+1,bits);

            while(temp<k && i<j)
            {
                del(v[i],bits);
                temp=value(j-i,bits);
                i++;
            }
            if(temp>=k){

            ans=ans+1ll*(j-i+1);
            }
            j++;
        }
        return ans;
    }


    long long countSubarrays(vector<int>& nums, int k) {
        int i=0;
        long long int sum=0;
        for(auto it:nums){
            sum+=it;
        }
        k++;
        long long ans2=atleastk(nums,k);
        long long ans1=atleastk(nums,k-1);

        return ans1-ans2;

        return 0;

    }
};
