class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        tl = []
        suba = []
        for num in nums:
            if num & k == k:
                tl.append(num)
            else:
                if tl:
                    suba.append(tl)
                    tl = []
        if tl:
            suba.append(tl)

        res = 0

        for tl in suba:
            tsl = []
            i = 0
            andres = tl[i]
            while i < len(tl):
                andres = tl[i] & andres
                if andres == k:
                    ts = [None, i]
                    andres = tl[i]
                    while i >= 0:
                        andres &= tl[i]
                        if andres == k:
                            ts[0] = i
                            tsl.append(ts)
                            break
                        i -= 1
                    i += 1
                    if i < len(tl):
                        andres = tl[i]
                else:
                    i += 1

            prev = 0

            for ts in tsl:
                add = (ts[0] - prev + 1) * (len(tl) - ts[1])
                res += add
                prev = ts[0] + 1


        return res
