class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        unordered_map<long long, long long> mp;
        long long ans = 0;
        for (auto& i : v) {
            unordered_map<long long, long long> temp;
            temp[i]++;
            if (k == i)
                ans++;
            for (auto& x : mp) {
                long long freq = x.second;
                long long val = x.first;
                long long chk = val & i;
                if (k==chk)
                    ans += freq;
                temp[chk] += freq;
            }
            mp = temp;
        }
        return ans;
    }
};
