#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll cnt=0;
        unordered_map<ll,ll>mp;
        for(int i=0;i<n;++i){
            unordered_map<ll,ll>m;
            if(nums[i]==k){
                cnt++;
            }
            m[nums[i]]=1;
            for(auto j:mp){
                ll num=j.first&nums[i];
                if(num==k){
                    cnt+=j.second;
                }
                m[num]+=j.second;
            }
            mp=m;
        }
        return cnt;
    }
};
