class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        long long t1 = 0;
        unordered_map<long long, long long> prev_occs;
        for (int cnt = 0; cnt < size; ++cnt) {
            unordered_map<long long, long long> curr_res; 
            if (nums[cnt] == k) {
                t1++;
            }
            curr_res[nums[cnt]] = 1;
            for (auto& [value, freq] : prev_occs) {
                long long new_and_result = value & nums[cnt];
                if (new_and_result == k) {
                    t1 += freq;
                }
                curr_res[new_and_result] += freq;
            }

            prev_occs = curr_res;
        }
        return t1;
    }
};
