class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n = nums.size(), cnt = 0;
        map<long long, long long>mpp;
        for(int i=0; i<n; i++){
            map<long long, long long>mpc; 
            if (nums[i] == k) {
                cnt++;
            }
            mpc[nums[i]] = 1;
            for (auto it:mpp) {
                long long num = it.first & nums[i];
                if (num == k) {
                    cnt += it.second;
                }
                mpc[num] += it.second;
            }        
            mpp =mpc;
        }     
        return cnt;
    }
};
