class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int n = array.size();
        long long totalCount = 0;
        unordered_map<long long, long long> previousResults;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currentResults;

            if (array[i] == target) {
                ++totalCount;
            }
            currentResults[array[i]] = 1;

            for (auto& [prevAndResult, freq] : previousResults) {
                long long newAndResult = prevAndResult & array[i];

                if (newAndResult == target) {
                    totalCount += freq;
                }

                currentResults[newAndResult] += freq;
            }

            previousResults = currentResults;
        }

        return totalCount;
    }
};
