class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        #define int long long
        int n = nums.size();
        int ans = 0;
        int solo = 0;
        unordered_map<int, int> prev, curr;
        for(int i=0;i<n;i++) {
            curr.clear();
            curr[nums[i]] = 1;
            for(auto&[key, v] : prev) {
                long long andd = key & nums[i];
                if(andd == k){
                    ans+=v;
                }
                curr[andd] += v;
            }
            solo += nums[i]==k;
            prev = curr;
        }
        #undef int
        return ans+0LL+solo;
    }
};
