class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
       int length = nums.size();
       long long totalCount = 0;

        unordered_map<long long, long long> previousResults;

        for (int index = 0; index < length; ++index) {
           unordered_map<long long, long long> currentResults;

    if (nums[index] == k) {
        ++totalCount;
    }
    currentResults[nums[index]] = 1;

    for (auto& [value, frequency] : previousResults) {
        long long andResult = value & nums[index];
        if (andResult == k) {
            totalCount += frequency;
        }
        currentResults[andResult] += frequency;
    }

    previousResults = currentResults;
}

return totalCount;
    }
};
