class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long cnt=0;
        unordered_map<long long,long long> m;

        for(int i=0;i<n;i++){
            unordered_map<long long,long long> r;
            if(nums[i]==k){
                cnt++;
            }
            r[nums[i]]=1;
            for(auto it:m){
                long long temp= it.first & nums[i];
                if(temp == k){
                    cnt+=it.second;
                }
                r[temp]+=it.second;
            }
            m=r;
        }
        return cnt;
    }
};
