class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0
        prev = {}

        for num in nums:
            new_res = {}
            if num == k:
                count += 1
            if (num & k) == k:
                new_res[num] = 1
            for and_result in prev:
                new_and = and_result & num
                if new_and == k:
                    count += prev[and_result]
                if (new_and & k) == k:
                    if new_and in new_res:
                        new_res[new_and] += prev[and_result]
                    else:
                        new_res[new_and] = prev[and_result]
            prev = new_res

        return count
