class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        ans = 0
        lastIter = defaultdict(int)
        thisIter = defaultdict(int)
        for x in nums:
            if x == k:
                ans += 1
            thisIter[x] += 1
            for y, cnt in lastIter.items():
                if x & y == k:
                    ans += cnt
                thisIter[x & y] += cnt
            lastIter, thisIter = thisIter, defaultdict(int)
        return ans
