class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        long long ans = 0;


        unordered_map<long long, long long> mp1;
         int n = nums.size();
        for(int i = 0;i<n;i++){
            unordered_map<long long, long long> mp2;
            if(nums[i] == k){
                ans++;
            }


            mp2[nums[i]] = 1;

            for(auto it : mp1){
                long long lol = it.first&nums[i];
                if(lol == k){
                    ans += it.second;
                }
                mp2[lol] += it.second; 
            }
            mp1 = mp2;
        }
        return ans;
    }
};
