class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long cnt=0;
        int n=nums.size();
        unordered_map<long long,long long>mp;
        for(int i=0;i<n;i++){
            unordered_map<long long,long long>mp2;
            if(nums[i]==k)cnt++;
            mp2[nums[i]]=1;
            for(auto it:mp){
                long long maxres=it.first & nums[i];
                if(maxres==k)cnt+=it.second;
                mp2[maxres]+=it.second;
            }
            mp=mp2;
        }
        return cnt;
    }
};
