class Solution {
public:
    #define ll long long
    ll countSubarrays(vector<int>& array, int target) {
        ll total = 0;
        unordered_map<ll, ll> prefix;

        for (int i = 0; i < array.size(); i++) {
            unordered_map<ll, ll> ans;
            if (array[i] == target)total++;
            ans[array[i]] = 1;
            for (auto& it : prefix) {
                ll value = it.first;
                ll frequency = it.second;
                ll newAndResult = value & array[i];

                if (newAndResult == target)total += frequency;
                ans[newAndResult] += frequency;
            }
            prefix = ans; 
        }

        return total;
    }
};
