class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cont = 0;

        unordered_map<long long, long long> mpp;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curntReslt; 

            if (nums[i] == k) {
                ++cont;
            }
            curntReslt[nums[i]] = 1;

            for (auto& [one, two] : mpp) {
                long long newReslt = one & nums[i];
                if (newReslt == k) {
                    cont += two;
                }
                curntReslt[newReslt] += two;
            }

            mpp = curntReslt;
        }

        return cont;
    }
};
