class Solution {
public:
long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();


        unordered_map<long long int, long long int> m;
 long long int count = 0;
        for (int i = 0; i < n; ++i) {


            if (nums[i] == k) {
                ++count;
            }
             unordered_map<long long int, long long int> c; 
            c[nums[i]] = 1;


            for (auto& [v, f] : m) {

                long long newa = v & nums[i];
                if (newa == k) {
                    count += f;
                }
                c[newa] += f;
            }

            m = c;
        }

        return count;
    }};
