class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    int n=nums.size();
    long long res=0;
    unordered_map<long long, long long> mp1;
    for (int i = 0; i < n; i++) {
        unordered_map<long long, long long> mp2; 
        if (nums[i] == k) {
            res++;
        }
        mp2[nums[i]] = 1;
        for (auto& it: mp1) {
            long long temp_val = it.first & nums[i];
            if (temp_val == k) {
                res += it.second;
            }
            mp2[temp_val] += it.second;
        }

        mp1 = mp2;
    }

       return res;
    }
};
