const int INF = 0x7fffffff;
typedef pair<int,int> PII;

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        vector<PII> bits; // 保存不同左端点出发，到同一右端点，的and值

        for(int i = 0; i < n; ++i){
            bits.push_back({INF,1});
            int ed = 0; // 表示当前and后，bits数组不重复的最后一个下标

            for(int j = 0; j < bits.size(); ++j){
                bits[j].first &= nums[i];

                if(j == 0) continue;
                else if(bits[j].first != bits[ed].first){
                    ed += 1;
                    bits[ed] = bits[j];
                }
                else{
                    bits[ed].second += bits[j].second;
                } 
            }
            bits.resize(ed+1);
            for(int j = 0; j < bits.size(); ++j){
                if(bits[j].first == k) ans += bits[j].second;
            }

        }
        return ans;
    }
};
