class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int size = array.size();
        long long ans = 0;
        unordered_map<ll, ll> previousResults;
        fr(index,0,size) {
            unordered_map<ll, ll> cr; 
            if (array[index] == target) {
                ++ans;
            }
            cr[array[index]] = 1;
            for (auto& [value, frequency] : previousResults) {
                long long newAndResult = value & array[index];
                if (newAndResult == target) {
                    ans += frequency;
                }
                cr[newAndResult] += frequency;
            }

            previousResults = cr;
        }
        return ans;
    }
};
