class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0
        previous_and_results = {}

        for i in range(n):
            current_and_results = {}
            if nums[i] == k:
                count += 1
            current_and_results[nums[i]] = current_and_results.get(nums[i], 0) + 1

            for and_result, frequency in previous_and_results.items():
                new_and_result = and_result & nums[i]
                if new_and_result == k:
                    count += frequency
                current_and_results[new_and_result] = current_and_results.get(new_and_result, 0) + frequency

            previous_and_results = current_and_results

        return count
