class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long tot = 0;
        unordered_map<long long, long long>prev;
        for(int i = 0; i<n; i++){
            unordered_map<long long, long long>curr;
            if(nums[i] == k){
                tot++;
            }
            curr[nums[i]] = 1;
            for(auto & [val, cnt] : prev){
                long long newVal = val & nums[i];
                if(newVal == k){
                    tot+=cnt;
                }
                curr[newVal] += cnt;
            }
            prev = curr;
        }
        return tot;
    }
};
