class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        map<long long,long long> prev;
        int check=0;
        long long ans=0;
        for(int i=0;i<n;i++){
            map<long long,long long> curr;
        curr[nums[i]]=1;
            for(auto &it : prev){
                if((it.first & nums[i])==k){
                        ans+=it.second;
                }    
            if(check==0) curr[(it.first & nums[i])]+=it.second;
            }
                if(!check) prev=curr;
        }
        for(int i=0;i<n;i++){
            if(nums[i]==k) ans++;
        }
        return ans;
    }
};
