class Solution {
public:
    long long f(vector<int>& a, int t) {
    long long n = a.size();
    long long c = 0;
    unordered_map<long long, long long> p;

    for (int i = 0; i < n; ++i) {
        unordered_map<long long, long long> q; 

        if (a[i] == t) ++c;

        q[a[i]] = 1;

        for (auto [k, v] : p) {
            long long b = k & a[i];
            if (b == t) c += v;
            q[b] += v;
        }

        p = q;
    }

    return c;
}
    long long countSubarrays(vector<int>& nums, int k) {
        return f(nums,k);
    }
};
