class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        int n=nums.size();
        vector<int> prev(32,0);
        for(int i=0;i<n;i++)
        {
            //zerowale is upperlimit
            //onewale is lower limit
            long long zerowale = 0, onewale=i+1;
            for(int l=0;l<32;l++)
            {
                if( ((nums[i]>>l)&1) == 1)
                {
                    prev[l]=max(prev[l]+1,1);
                }
                else
                {
                    prev[l]=0;
                }
                // cout<<prev[l]<<" ";
                if(((k>>l)&1)==1)
                {
                    onewale=min(onewale,(long long)prev[l]);
                }
                else
                {
                    zerowale=max(zerowale,prev[l]+1ll);
                }
            }
            ans+=max(onewale-zerowale+1,0ll);
            // cout<<onewale<<" "<<zerowale<<endl;
        }
        return ans;

