class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

         long long count = 0;
        int n = nums.size();
        std::unordered_map<int, int> and_freq;

        for (int i = 0; i < n; ++i) {
            std::unordered_map<int, int> new_and_freq;
            for (auto& [and_result, freq] : and_freq) {
                int new_and_result = and_result & nums[i];
                new_and_freq[new_and_result] += freq;
            }
            new_and_freq[nums[i]]++;
            and_freq = new_and_freq;

            for (auto& [and_result, freq] : and_freq) {
                if (and_result == k) {
                    count += freq;
                }
            }
        }

        return count;

    }
};
