class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
   int n = nums.size();
    long long int count = 0;

    unordered_map<int, int> dp;

    for (int i = 0; i < n; ++i) {
        unordered_map<int, int> newDp;

        if (nums[i] == k) {
            count++;
        }
        newDp[nums[i]]++;

        for (auto& p : dp) {
            int newAND = p.first & nums[i];
            if (newAND == k) {
                count += p.second;
            }
            newDp[newAND] += p.second;
        }
        dp = newDp;
    }
     return count;
    }
};
