class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size(); 
        long long cnt = 0; 

        unordered_map<long long, long long> prev; 

        for (int i = 0; i < n; ++i) { 
            unordered_map<long long, long long> curr;  

            if (nums[i] == k) { 
                ++cnt; 
            } 
            curr[nums[i]] = 1; 

            for (auto& [v, f] : prev) { 
                long long temp = v & nums[i]; 
                if (temp == k) { 
                    cnt += f; 
                } 
                curr[temp] += f; 
            } 

            prev = curr; 
        } 

        return cnt; 
    }
};
