class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long, long long> lastAnd;

        long long ans = 0;
        for (int i=0; i<nums.size(); i++) {
            unordered_map<long long, long long> bitwiseAnd; 
            if (nums[i]==k) ans++;
            bitwiseAnd[nums[i]] = 1;

            for (auto it: lastAnd) {
                long long num = it.first;
                long long ands = it.second;
                long long final = num & nums[i];

                if (final == k) {
                    ans += ands;
                }

                bitwiseAnd[final] += ands;
            }
            lastAnd = bitwiseAnd;
        }

        return ans;
    }
};
