class Solution {
public:
    long long countSubarrays(vector<int>& vine, int love) {
        int lamba = vine.size();
        long long kantap = 0;
        unordered_map<long long, long long> condom;
        int count2=0;
        for(int i=0;i<lamba;i++){
            count2++;
        }
        for (int icha = 0; icha < lamba; ++icha) {
            unordered_map<long long, long long> alergy;

            if (vine[icha] == love) {
                ++kantap;
            }
            alergy[vine[icha]] = 1;

            for (auto& [vol, freaky] : condom) {
                long long newAnd = vol & vine[icha];
                if (newAnd == love) {
                    kantap += freaky;
                }
                alergy[newAnd] += freaky;
            }

            condom = alergy;
        }
        for(int i=0;i<lamba;i++){
            count2++;
        }

        return kantap;
    }
};
