class Solution {

    typedef long long ll;

public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<ll,ll> mp1;

        ll ans=0;
        for(int i=0;i<nums.size();i++){
            unordered_map<ll,ll> mp2;
            if(nums[i]==k) ans++;

            mp2[nums[i]]=1;

            for(auto &x:mp1){
                ll temp=(x.first&nums[i]);
                if(temp==k) ans+=x.second;

                mp2[temp]+=x.second;
            }

            mp1=mp2;
        }

        return ans;
    }
};
