class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long cnt=0;
        int n=nums.size();
        unordered_map<long long,long long> mpp;

        for(int i=0;i<n;i++)
        {
            unordered_map<long long,long long> mpp1;
            if(nums[i]==k)  cnt++;
             mpp1[nums[i]]=1;

            for(auto& [numb,times]:mpp)
            {
                long long newres=numb & nums[i];
                if (newres==k)  cnt+=times;
                 mpp1[newres]+=times;
            }
           mpp=mpp1;


        }
        return cnt;


    }
};
