class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    unordered_map<long long,long long>mp;
// mp[0]=1;
            long long cnt = 0;
    for (  long long  i=0;i<nums.size();i++) {
        if (nums[i] == k) 
          cnt++;
         unordered_map<long long, long long>mp2;
        mp2[nums[i]] = 1;
        for (auto it:mp) {
          long long val=it.first&nums[i];
             mp2[val]+=it.second;
          if (val==k) {
                cnt+=it.second;
          }

        }
        mp=mp2;
        mp2.clear();
    }
    return cnt;
    }
};
