int mod=1e9+7;
ll power(ll x,ll n){if(n==1)return x;ll a=power(x,n/2)%mod;if(n&1)return (a*a*x)%mod;return (a*a )%mod;} 
vector<int>prime(int n){vector<int>a(n+1,1);a[0]=0,a[1]=0;for(int i=2;i<=n;i++){if(a[i]){for(int j=i*i;j<=n;j+=i)a[j]=0;}}vector<int>ans;for(int i=2;i<=n;i++){if(a[i]==1)ans.push_back(i);}return ans;}
vector<int>sieve(int n){vector<int>a(n+1,1);a[0]=0,a[1]=0;for(int i=2;i<=n;i++){if(a[i]){for(int j=i*i;j<=n;j+=i)a[j]=0;}}return a;}


class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        vector<ll>k1(32,0);ll i=0,n=nums.size();
        while(k>0){
            if(k%2)k1[i]=1;
            k/=2;i++;
        }
        vector<vector<ll>>arr(32,vector<ll>(n,0));
        for(ll i=0;i<n;i++){
            ll a=nums[i];ll j=0;
            while(a>0){
                if(a%2)arr[j][i]=1;
                a/=2;j++;
            }
        }
        for(ll i=0;i<32;i++){
            ll nr=n;
            for(ll j=n-1;j>=0;j--){
                if(arr[i][j]==0)nr=j;
                arr[i][j]=nr;   
            }
        }

        ll ans=0;
        for(int i=0;i<n;i++){
            ll mn=n,mx=i;
            for(ll j=0;j<32;j++){
                if(k1[j]==0)mx=max(arr[j][i],mx);
                else mn=min(mn,arr[j][i]);
            }
            if(mn-mx>0)ans+=mn-mx;

        }return ans;
    }
};
