class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        unordered_map<int, long long> dp;
        for (int i : nums) {
            unordered_map<int, long long> curr;
            if (i == k) ans++;
            curr[i] = 1;
            for (auto& [key, value] : dp) {
                if ((key & i) == k) {
                    ans += value;
                }
                curr[(key & i)] += value;
            }
            dp = curr;

        }
        return ans;
    }
};
