class Solution {
public:

    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();

        long long ans = 0;

        unordered_map<long long, long long> m;
        for (int i=0;i<n;i++) {
            unordered_map<long long, long long> mac; 

            if (nums[i]==k) {
                ans++;
            }

            mac[nums[i]] = 1;
            for (auto j : m) {
                if((j.first & nums[i])==k) ans += j.second;

                mac[j.first & nums[i]] += j.second;
            }

            m = mac;
        }
        return ans;

    }
};
