    long long countSubarrays(vector<int>& a, int k) {



        long long ans = 0;

        map<long long, long long> mp;
        for(int i=0;i<a.size();i++) 
        {
            map<long long, long long> mp2;

            if(a[i] == k) 
            {
                ans++ ;
            }


            mp2[a[i]] = 1;


            for(auto &j:mp) 
            {
                long long res = j.first & a[i];

                if(res == k) 
                {
                    ans += j.second ;
                }
                mp2[res] += j.second ;
            }

            mp = mp2;
        }


        return ans ;
    }
