class Solution {
public:
   long long countSubarrays(vector<int>& nums, int k) {
        long long result = 0;
        int n = nums.size();
        unordered_map<int, int> andCount;

        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> newAndCount;
            for (auto& [prevAnd, count] : andCount) {
                int currentAnd = prevAnd & nums[i];
                newAndCount[currentAnd] += count;
            }
            newAndCount[nums[i]]++;
            andCount = newAndCount;

            result += andCount[k];
        }

        return result;
    }
};
