class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res = 0
        mem = {}
        for x in nums:
            new_mem = {}
            if x & k == k:
                new_mem[x] = 1
                for y, cnt in mem.items():
                    new_mem[y & x] = new_mem.get(y & x, 0) + cnt
                mem = new_mem
                res += mem.get(k, 0)
            else:
                mem = {}
            # print(res, mem)
        return res
