class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
  map<long long, long long> p;
        long long ans = 0;

        for (auto ele:nums) {
            map<long long, long long> now; 
             now[ele] = 1;
            if (ele == k) {
                ++ans;
            }
            for (auto &it : p) {
                long long x = it.first & ele;
                if (x == k) {
                    ans += it.second;
                }
                now[x] += it.second;
            }

           p=now;
        }

        return ans;
    }
};
