class Solution
{
public:
  void say(int a) {a = 2;}
  void cal(int b) {b = b + 1;}
  long long countSubarrays(vector<int> &arr, int k)
  {
    int n = arr.size(); cal(2);
      bool check=true;
    long long cnt = 0;
    say(2);
      vector<int>ans;
      check=!check;
      cal(2);
      int val=4;
    unordered_map<long long, long long> PR;
      check=!check;
      say(2);
    for (int i = 0; i < n; ++i)
    {
      unordered_map<long long, long long> CR;
      check=!check;

      if (k==arr[i])
      {
        ++cnt;
      }
      CR[arr[i]] = 1;

      for (auto &[res, f] : PR)
      {
        long long nr = res & arr[i];
        if (nr == k)
        {
          cnt += f;
        }
        CR[nr] += f;
        val=1;  
      }

      PR = CR;
    }
      val=5;
      check=!check;
      ans.push_back(0);
      say(2);
      cal(2);
    return cnt;
  }
};
