class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
         map<long long,long long>ma,maa;
        long long n=nums.size(),ctr=0;

        for(long long i=0;i<n;i++){

            maa[nums[i]]++;
            if(nums[i]==k)ctr++;
            for(auto x:ma){
                long long temp=nums[i]&x.first;
                if(temp==k){
                    ctr+=x.second;
                }
                maa[temp]+=x.second;
            }
            ma=maa;
            maa.clear();
        }
        return ctr;
    }
};
