class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int size = array.size();
        long long result = 0;
        unordered_map<long long, long long> previousState;

        for (int i = 0; i<size; i++) {
            unordered_map<long long, long long> currentState;

            if (array[i] == target) result++;

            currentState[array[i]] = 1;

            for (auto& [val, count] : previousState) {
                long long andResult = val & array[i];
                if (andResult == target) {
                    result += count;
                }
                currentState[andResult] += count;
            }

            previousState = currentState;
        }

        return result;
    }
};
