class Solution {
public:
    #define ll long long
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll cnt=0;
        unordered_map<ll,ll>mp;
        for(int i=0;i<n;i++){
            unordered_map<ll,ll>mp1;
            if(nums[i]==k){
                cnt++;
            }
            mp1[nums[i]]++;
            for(auto it:mp){
                ll res=it.first & nums[i];
                if(res==k){
                    cnt+=it.second;
                }
                mp1[res]+=it.second;
            }
            mp=mp1;
        }
        return cnt;
    }
};
