class Solution {
public:
    void f(vector<int> &nums, int k, long long &ans){
        unordered_map<long long, long long> mpp1;
        for(int i=0;i<nums.size();i++){
            unordered_map<long long, long long> mpp2;
            if(nums[i]==k){
                ans++;
            }
            mpp2[nums[i]] = 1;
            for(auto it : mpp1){
                long long ind = it.first & nums[i];
                if(ind==k){
                    ans+=it.second;
                }
                mpp2[ind]+=it.second;
            }
            unordered_map<long long, long long> mpp3;
            for(auto it : mpp2){
                mpp3[it.first] = it.second;
            }
            mpp1 = mpp3;
        }
    }
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        f(nums, k, ans);
        return ans;
    }
};
