class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        vector<vector<int>>pf(n+1,vector<int>(31,0));
        for(int i=0;i<n;++i){
            int num=nums[i];
            for(int j=0;j<31;++j){
                if((num&(1<<j))){
                    pf[i][j]=1;
                    if(i>0) pf[i][j]+=pf[i-1][j];
                }
            }
            int othermx=0;
            int mn=1e9;
            int f=0;
            for(int j=0;j<31;++j){
                if((k&(1<<j))){
                    if(pf[i][j]==0){
                        f=1;
                        break;
                    }
                    mn=min(mn,pf[i][j]);
                }
                else{
                    othermx=max(othermx,pf[i][j]);
                }
            }
            // cout<<num<<" ";
            // cout<<mn<<" "<<othermx<<" "<<f<<endl;
            if(f==0){
                if(k==0){
                    ans+=i-othermx+1;
                }
                else if(mn>othermx){
                    ans+=mn-othermx;
                }
            }
        }
        return ans;
    }
};
