typedef long long ll;
#define sz(x) (int)x.size()

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int K) {
        ll last[31], n {sz(nums)};;        
        memset(last,-1,sizeof(last));
        ll ans {};
        for (int i = 0; i < n; ++i) {
            ll z {i}, o {-1};
            for (int k = 0; k < 31; ++k) {
                if ((nums[i]&(1<<k))==0) last[k]=i;
                if (K&(1<<k)) o=max(o,last[k]);
                else z=min(z,last[k]);
            }
            if (z>o) ans+=z-o;
        }
        return ans;
    }
};
