using pll = pair<ll,ll>;
using vl  = vector<ll>;
using vi  = vector<int>;

class Solution {
public:
    long long countSubarrays(vector<int>& v, int K) {
         ll n=v.size();
         vector<vl>dp(n+1,vl(31,0));
         rep(i,0,31){
              for(int j=0;j<n;j++){
                   dp[j+1][i]=dp[j][i]+((v[j]>>i)&1);
              }
         } 

         auto check=[&](ll l,ll r,ll k){
              ll ans=0;
              for(int i=0;i<31;i++){
                   if((dp[r][i]-dp[l-1][i])==r-l+1){
                        ans+=(1<<i);
                   }
              }
              return ans>=k;
         };
        ll ret=0;
         for(int i=0;i<n;i++){
              ll l=i+1,k=K;
              ll lo=l,hi=n+1,ans=i;
              while(lo<hi){
                   ll mid=(lo+hi)/2;
                   if(check(l,mid,k)){
                        ans=mid;
                        lo=mid+1;
                   }
                   else{
                        hi=mid;
                   }
              }
              ll x=ans;
              k=K+1;
              l=i+1;
              lo=l,hi=n+1,ans=i;
              while(lo<hi){
                   ll mid=(lo+hi)/2;
                   if(check(l,mid,k)){
                        ans=mid;
                        lo=mid+1;
                   }
                   else{
                        hi=mid;
                   }
              }
             // cout<<i<<" "<<x<<" "<<ans<<endl;
             ret+=(x-ans);
         }
        return ret;
    }
};
