class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long c=0;
        unordered_map<long long,long long>mp;
        for(int i=0;i<n;i++){
            unordered_map<long long,long long>m;
            if(nums[i]==k)c++;
            m[nums[i]]=1;
            for(auto it:mp){
                int h = it.first&nums[i];
                if(h==k)c+=it.second;
                if(m.find(h)!=m.end())m[h]+=it.second;
                else m[h]=it.second;
            }
            mp=m;
        }
        return c;
    }
};
